mapper.update:
update
	   notice
   set 
       notice_title = #{noticeTitle}
     , notice_content = #{noticeContent}
 where notice_no = #{noticeNo}

mapper.select:
select
	   count(*) count
  from tlike
 where notice_no = #{noticeNo}
   and emp_no = #{empNo}

mapper.insert:
insert
  into tlike
  (
  	notice_no
  , emp_no
  )
  values
  (
  	#{noticeNo}
  , #{empNo}
  )

mapper.delete:
delete
  from tlike
 where notice_no = #{noticeNo}
   and emp_no = #{empNo}
